//============================================================
//               Helper functions to filter data
//============================================================

export const getFeaturedDestinations = (limit = 8) => {
  return dummyDestinations.filter(d => d.featured).slice(0, limit);
};

export const getPublicItineraries = (limit = 5) => {
  return dummyItineraries
    .filter(i => i.isPublic && i.status === 'published')
    .slice(0, limit);
};

export const getUserItineraries = (userId, status = null) => {
  let itineraries = dummyItineraries.filter(i => i.userId === userId);
  if (status) {
    itineraries = itineraries.filter(i => i.status === status);
  }
  return itineraries;
};

export const getItineraryById = (id) => {
  return dummyItineraries.find(i => i._id === id);
};

export const getDestinationById = (id) => {
  return dummyDestinations.find(d => d._id === id);
};

export const getUserById = (id) => {
  return dummyUsers.find(u => u._id === id || u.id === id);
};

//============================================================
//                     Mock API Delay
//============================================================

export const mockDelay = (ms = 500) => {
  return new Promise(resolve => setTimeout(resolve, ms));
};

//============================================================
//                Exporting the data objects
//============================================================

export default {
  users: dummyUsers,
  destinations: dummyDestinations,
  itineraries: dummyItineraries,
  helpers: {
    getFeaturedDestinations,
    getPublicItineraries,
    getUserItineraries,
    getItineraryById,
    getDestinationById,
    getUserById,
    mockDelay
  }
};
